import React,{useRef, useState} from 'react'
import Home from './components/Home/Home'
import Navigation from './components/Navigation/Navigation'
import Aboutme from './components/Aboutme/Aboutme'
import TechStack from './components/TechStack/TechStack'
import Projects from './components/Projects/Projects'
import Blogs from './components/Blogs/Blogs'
import Contact from './components/Contact/Contact'
import Footer from './components/Footer/Footer'

function App() {
	const intialArray = ['active','','','','' ];
	const[activeArray, setActivearray] = useState(intialArray);
    const techRef = useRef(null);
    const aboutRef = useRef(null);
    const projectsRef = useRef(null);
    const blogsRef = useRef(null);
    const contactRef = useRef(null);    
    const executeTechScroll = () => techRef.current.scrollIntoView(); 
    const executeAboutScroll = () => aboutRef.current.scrollIntoView(); 
    const executeProjectsScroll = () => projectsRef.current.scrollIntoView(); 
    const executeBlogsScroll = () => blogsRef.current.scrollIntoView(); 
    const executeContactScroll = () => contactRef.current.scrollIntoView(); 
  return (
    <div class="body-wrappper">
    	<Home></Home>
    	<nav class="flex link-wrap desk">
          <div class="page-link active" dest="home">Home</div>
          <div class="page-link" dest="about" onClick={executeAboutScroll}>About</div>
          <div class="page-link" dest="techstack" onClick={executeTechScroll}>Tech Stack</div>
          <div class="page-link" dest="portfolio" onClick={executeProjectsScroll}>portfolio</div>
          <div class="page-link" dest="blog" onClick={executeBlogsScroll}>blog</div>
          <div class="page-link" dest="contact" onClick={executeContactScroll}>contact</div>
      	</nav>
    	<Aboutme refAboutProp={aboutRef}></Aboutme>
    	<TechStack refTechProp={techRef}></TechStack>    	
    	<Projects refProjectsProp={projectsRef}></Projects>  	
    	<Blogs refBlogsProp={blogsRef}></Blogs> 	
    	<Contact refContactProp={contactRef}></Contact>
    	<Footer></Footer>
    </div>

  );
}

export default App;
