import React,{useRef} from 'react'
import Home from './components/Home/Home'
import Navigation from './components/Navigation/Navigation'
import Aboutme from './components/Aboutme/Aboutme'
import TechStack from './components/TechStack/TechStack'
import Projects from './components/Projects/Projects'
import Blogs from './components/Blogs/Blogs'
import Contact from './components/Contact/Contact'
import Footer from './components/Footer/Footer'

function App() {
    const techRef = useRef(null);
    const aboutRef = useRef(null);
    const projectsRef = useRef(null);
    const blogsRef = useRef(null);
    const contactRef = useRef(null);    
    const executeScroll = (refprop) => {
    	refprop.current.scrollIntoView();
    	} 
  return (
    <div class="body-wrappper">
    	<Home></Home>
    	<nav class="flex link-wrap desk">
          <div class="page-link active" dest="home">Home</div>
          <div class="page-link" dest="about">About</div>
          <button class="page-link" dest="techstack" onClick={executeScroll(refprop)}>Tech Stack</button>
          <div class="page-link" dest="portfolio">portfolio</div>
          <div class="page-link" dest="blog">blog</div>
          <div class="page-link" dest="contact">contact</div>
      	</nav>
    	<Aboutme refAboutProp={aboutRef}></Aboutme>
    	<TechStack refTechProp={techRef}></TechStack>    	
    	<Projects refProjectsProp={projectsRef}></Projects>  	
    	<Blogs refBlogsProp={blogsRef}></Blogs> 	
    	<Contact refContactProp={contactRef}></Contact>
    	<Footer></Footer>
    </div>

  );
}

export default App;
