import React from 'react';
import ReactDOM from 'react-dom'
import { useState } from 'react';
import jellybeat_1 from './images/jellybeat/HomePage_1.png';
import jellybeat_2 from './images/jellybeat/HomePage_2.png';
import jellybeat_5 from './images/jellybeat/Checkout.png';
import jellybeat_3 from './images/jellybeat/Product_Listing_Page.png';
import jellybeat_4 from './images/jellybeat/Product_View_Page.png';



var project=[
{
	id:1,
	title: 'Retail application',
	tech: 'React, Laravel',
	description: 'Roambi provides analytics, reporting, and business intelligence for companies to use on the go. A Wordpress hosted site written in PHP and Javascript with Hubspot Integration.',
	link: '',
	imagecount: 3,
	imagelinks:'{jellybeat_1, jellybeat_2, jellybeat_3, jellybeat_4, jellybeat_5}',
},
]


const ProjectModel = (props) => {

  return (
    <div>
      {ReactDOM.createPortal(
        <Backdrop onClick={props.onConfirm} />,
        document.getElementById('backdrop-root')
      )}
      {ReactDOM.createPortal(
        <ModalOverlay 
          title={project[props.no]['title']}
          tech={project[props.no]['tech']}
          description={project[props.no]['description']}
          images={project[props.no]['imagelinks']}
          link={project[props.no]['link']}
          imagescount={project[props.no]['imagescount']}
          onConfirm={props.onConfirm}/>,

        document.getElementById('modal-root')
      )}
    </div>
  );
};

const ModalOverlay = (props) => {
  console.log(props);
  //
  

  return (
    <div class="modal">
        <div class="carousel-wrap" style="width: 700px;">
            <div class="window">
              <div id="carousel" style="left: -700px; transform: translateX(0px);" class="">
                    <img class="slide" src={props.images[0]}/>
                    <img class="slide" src={props.images[1]}/>
                    <img class="slide" src={props.images[2]}/>
              <i class="mdi mdi-chevron-left" id="prev"></i>
              <i class="mdi mdi-chevron-right" id="next"></i>
            </div>
          </div>
         </div>
      <div class="info-box">
        <div class="title">{props.title}</div>
          <div class="tag">{props.tech}</div>
            <div class="detail">{props.description}</div>
            <a href={props.link} target="_blank"><div class="button visible">
                <i class="mdi mdi-open-in-new"></i>VIEW SITE
          </div></a>
      </div>

      <footer>
        <button class="close" id="trigger_close" onClick={props.onConfirm}>Close</button>
      </footer>
    </div>
  );
};

const Backdrop = (props) =>{
  return (<div class="backdrop" onConfrim={props.onClick} />);
};


export default ProjectModel;
